// Aggregate adds the given aggregation functions to the selector query.
func (ts *TreasurySelect) Aggregate(fns ...AggregateFunc) *TreasurySelect {
	ts.fns = append(ts.fns, fns...)
	return ts
}

// Scan applies the selector query and scans the result into the given value.
func (ts *TreasurySelect) Scan(ctx context.Context, v any) error {
	ctx = setContextOp(ctx, ts.ctx, ent.OpQuerySelect)
	if err := ts.prepareQuery(ctx); err != nil {
		return err
	}
	return scanWithInterceptors[*TreasuryQuery, *TreasurySelect](ctx, ts.TreasuryQuery, ts, ts.inters, v)
}

func (ts *TreasurySelect) sqlScan(ctx context.Context, root *TreasuryQuery, v any) error {
	selector := root.sqlQuery(ctx)
	aggregation := make([]string, 0, len(ts.fns))
	for _, fn := range ts.fns {
		aggregation = append(aggregation, fn(selector))
	}
	switch n := len(*ts.selector.flds); {
	case n == 0 && len(aggregation) > 0:
		selector.Select(aggregation...)
	case n != 0 && len(aggregation) > 0:
		selector.AppendSelect(aggregation...)
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := ts.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

// Modify adds a query modifier for attaching custom logic to queries.
func (ts *TreasurySelect) Modify(modifiers ...func(s *sql.Selector)) *TreasurySelect {
	ts.modifiers = append(ts.modifiers, modifiers...)
	return ts
}
